import React, { useState, useEffect } from "react";
import axios from "axios";

const EditFilmPage = ({ match }) => {
  const [film, setFilm] = useState({});
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState("");

  useEffect(() => {
    const fetchFilm = async () => {
      try {
        const response = await axios.get(`/api/films/${match.params.id}`);
        setFilm(response.data);
        setTitle(response.data.title);
        setDescription(response.data.description);
        setImage(response.data.image);
      } catch (error) {
        console.error(error);
      }
    };

    fetchFilm();
  }, [match.params.id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.put(`http://localhost:3000/films/${match.params.id}`, {
        title,
        description,
        image,
      });
      alert("Film updated successfully");
      // Redirect or do something else after successful update
    } catch (error) {
      console.error(error);
      alert("Failed to update film");
    }
  };

  return (
    <div>
      <h2>Edit Film</h2>
      <form onSubmit={handleSubmit}>
        <div>
          <label>Title:</label>
          <input type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
        </div>
        <div>
          <label>Description:</label>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)}></textarea>
        </div>
        <div>
          <label>Image:</label>
          <input type="text" value={image} onChange={(e) => setImage(e.target.value)} />
        </div>
        <button type="submit">Update Film</button>
      </form>
    </div>
  );
};

export default EditFilmPage;
