import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";

const filmList = () => {
  const [film, setfilm] = useState([]);

  useEffect(() => {
    getfilm();
  }, []);

  const getfilm = async () => {
    const response = await axios.get("http://localhost:3000/films");
    setfilm(response.data);
  };

  const deletefilm = async (filmId) => {
    try {
      await axios.delete(`http://localhost:5000/films/${filmId}`);
      getfilm();
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="container mt-5">
      <Link to="/add" className="button is-success">
        Add New
      </Link>
      <div className="columns is-multiline mt-2">
        {products.map((films) => (
          <div className="column is-one-quarter" key={film.id}>
            <div className="card">
              <div className="card-image">
                <figure className="image is-4by3">
                  <img src={film.url} alt="Image" />
                </figure>
              </div>
              <div className="card-content">
                <div className="media">
                  <div className="media-content">
                    <p className="title is-4">{film.title}</p>
                    <p className="title is-4">{film.description}</p>
                  </div>
                </div>
              </div>

              <footer className="card-footer">
                <Link to={`edit/${film.id}`} className="card-footer-item">
                  Edit
                </Link>
                <a onClick={() => deletefilm(film.id)} className="card-footer-item">
                  Delete
                </a>
              </footer>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default filmList;
