import axios from "axios";
import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

const AddMovie = ({ section }) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [file, setImage] = useState(null);
  const navigate = useNavigate();

  const handleImageUpload = (e) => {
    const file = e.target.files[0];
    setImage(file);
  };
  const saveFilms = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", title);
    formData.append("description", description);
    formData.append("file", file);
    try {
      await axios.post("http://localhost:3000/films", formData, {
        headers: {
          "Content-type": "multipart/form-data",
        },
      });
      navigate("/");
      // window.location.reload();
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="add-movie" id="addfilm">
      <h3>Tambahkan Film Baru</h3>
      <Form onSubmit={saveFilms}>
        <Form.Group controlId="title">
          <Form.Label>Judul</Form.Label>
          <Form.Control type="text" placeholder="Masukkan judul film" value={title} onChange={(e) => setTitle(e.target.value)} required />
        </Form.Group>
        <Form.Group controlId="description">
          <Form.Label>Deskripsi</Form.Label>
          <Form.Control as="textarea" placeholder="Masukkan deskripsi film" value={description} onChange={(e) => setDescription(e.target.value)} required />
        </Form.Group>
        <Form.Group controlId="image">
          <Form.Label>Unggah Gambar</Form.Label>
          <Form.Control type="file" accept="image/*" onChange={handleImageUpload} required />
        </Form.Group>
        <Button variant="primary" type="submit">
          Tambahkan
        </Button>
      </Form>
    </div>
  );
};

export default AddMovie;
