import React, { useState, useEffect } from "react";
import axios from "axios";
import { Form, Button } from "react-bootstrap";
import { useNavigate } from "react-router-dom";

const EditFilmPage = ({ match }) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [image, setImage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    const fetchFilm = async () => {
      try {
        const response = await axios.get(`http://localhost:3000/api/film/${match.params.id}`);
        const { title, description, image } = response.data;
        setTitle(title);
        setDescription(description);
        setImage(image);
      } catch (error) {
        console.error(error);
      }
    };

    fetchFilm();
  }, [match.params.id]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      await axios.put(`http://localhost:3000/api/film/${match.params.id}`, {
        title,
        description,
        image,
      });
      alert("Film updated successfully");
      navigate("/");
    } catch (error) {
      console.error(error);
      alert("Failed to update film");
    }
  };

  return (
    <div>
      <h2>Edit Film</h2>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="title">
          <Form.Label>Title:</Form.Label>
          <Form.Control type="text" value={title} onChange={(e) => setTitle(e.target.value)} />
        </Form.Group>
        <Form.Group controlId="description">
          <Form.Label>Description:</Form.Label>
          <Form.Control as="textarea" value={description} onChange={(e) => setDescription(e.target.value)} />
        </Form.Group>
        <Form.Group controlId="image">
          <Form.Label>Image:</Form.Label>
          <Form.Control type="text" value={image} onChange={(e) => setImage(e.target.value)} />
        </Form.Group>
        <Button variant="primary" type="submit">
          Update Film
        </Button>
      </Form>
    </div>
  );
};

export default EditFilmPage;
