import React, { useState, useEffect } from "react";
import { Navbar, Container, Nav, Card, Row, Col, Image, Button } from "react-bootstrap";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import axios from "axios";
import "../style/landingPage.css";

const Trending = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    fetchMovies();
  }, []);

  const fetchMovies = async () => {
    try {
      const response = await axios.get("http://localhost:3000/films");
      setMovies(response.data);
    } catch (error) {
      console.error("Terjadi kesalahan:", error);
    }
  };

  return (
    <div className="trending">
      <Navbar variant="dark">
        <Container>
          <Navbar.Brand as={Link} to="/">
            UASFILM
          </Navbar.Brand>
          <Nav>
            <Nav.Link as={Link} to="/add">
              ADD MOVIE
            </Nav.Link>
            <Nav.Link as={Link} to="/edit">
              Edit MOVIE
            </Nav.Link>
            <Nav.Link href="#film">KUMPULAN FILM</Nav.Link>
          </Nav>
        </Container>
      </Navbar>

      <Container className=" intro myBG text-white text-center d-flex justify-content-center align-items-center">
        <div>
          <h2 className="mb-4">NONTON GRATIS</h2>
          <h2>GAK PAKE KARCIS</h2>
          <div className="introButton mt-4 text-center">
            <Button variant="dark" as={Link} to="/trending">
              Lihat Semua List
            </Button>
          </div>
        </div>
      </Container>

      <Container id="film">
        <br />
        <h1 className="text-white">FILMS</h1>
        <br />
        <Row>
          {movies.map((movie) => (
            <Col md={4} className="movieWrapper" key={movie.id}>
              <Card className="movieImage">
                <Image src={movie.url} alt={movie.title} className="images" />
                <div className="bg-dark">
                  <div className="p-2 m-1 text-white">
                    <Card.Title className="text-center">{movie.title}</Card.Title>
                    <Card.Text className="text-left">{movie.description}</Card.Text>
                    <Card.Text className="text-left">Last updated {new Date(movie.updatedAt).toLocaleDateString()}</Card.Text>
                  </div>
                </div>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </div>
  );
};

export default Trending;
