import React, { useState, useEffect } from "react";
import axios from "axios";
import { Link } from "react-router-dom";
import { Navbar, Nav, Container, Card, Row, Col, Image } from "react-bootstrap";

const Trending = () => {
  const [movies, setMovies] = useState([]);

  useEffect(() => {
    fetchMovies();
  }, []);

  const fetchMovies = async () => {
    try {
      const response = await axios.get("http://localhost:3000/films");
      setMovies(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div style={{ backgroundColor: "#000", color: "#fff" }}>
      <Navbar variant="dark" bg="dark">
        <Container>
          <Navbar.Brand href="/">UASFILM</Navbar.Brand>
          <Nav>
            <Link to="/add" href="#addfilm">
              ADD MOVIE
            </Link>
            <Nav.Link href="#film">KUMPULAN FILM</Nav.Link>
          </Nav>
        </Container>
      </Navbar>
      <Container id="film">
        <br />
        <h1 className="text-white">FILMS</h1>
        <br />
        <Row>
          {movies.map((movie) => (
            <Col md={4} className="movieWrapper" key={movie.id}>
              <Card className="movieImage">
                <img src={movie.url} alt={movie.title} className="images" />
                <div className="bg-dark">
                  <div className="p-2 m-1 text-white">
                    <Card.Title className="text-center">{movie.title}</Card.Title>
                    <Card.Text className="text-left">{movie.description}</Card.Text>
                    <Card.Text className="text-left">Last updated {movie.updatedAt}</Card.Text>
                  </div>
                </div>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </div>
  );
};

export default Trending;
