import React, { useState } from "react";
import { Form, Button } from "react-bootstrap";

const AddMovie = ({ section }) => {
  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();

    // Buat objek data film
    const movieData = {
      title,
      description,
    };

    // Kirim data film ke API backend (ganti URL_API dengan URL endpoint API Anda)
    fetch("URL_API/movies", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(movieData),
    })
      .then((response) => response.json())
      .then((data) => {
        console.log("Film berhasil ditambahkan:", data);
        // Lakukan manipulasi komponen atau pengecekan status tambahan jika diperlukan
      })
      .catch((error) => {
        console.error("Terjadi kesalahan:", error);
        // Handle kesalahan atau tampilkan pesan error jika diperlukan
      });

    // Reset form setelah mengirim data
    setTitle("");
    setDescription("");
  };

  return (
    <div className="add-movie">
      <h3>Tambahkan Film Baru</h3>
      <Form onSubmit={handleSubmit}>
        <Form.Group controlId="title">
          <Form.Label>Judul</Form.Label>
          <Form.Control type="text" placeholder="Masukkan judul film" value={title} onChange={(e) => setTitle(e.target.value)} required />
        </Form.Group>
        <Form.Group controlId="description">
          <Form.Label>Deskripsi</Form.Label>
          <Form.Control as="textarea" placeholder="Masukkan deskripsi film" value={description} onChange={(e) => setDescription(e.target.value)} required />
        </Form.Group>
        <Button variant="primary" type="submit">
          Tambahkan
        </Button>
      </Form>
    </div>
  );
};

export default AddMovie;
