import React, { useState, useEffect } from "react";
import { Card, CardDeck } from "react-bootstrap";
import axios from "axios";

const HomePage = () => {
  const [films, setFilms] = useState([]);

  useEffect(() => {
    fetchFilms();
  }, []);

  const fetchFilms = async () => {
    try {
      const response = await axios.get("http://localhost:3000/films");
      setFilms(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  return (
    <div className="container">
      <Navbar bg="light" expand="lg">
        <Container>
          <Navbar.Brand as={Link} to="/">
            Movie App
          </Navbar.Brand>
          <Navbar.Toggle aria-controls="basic-navbar-nav" />
          <Navbar.Collapse id="basic-navbar-nav">
            <Nav className="mr-auto">
              <Nav.Link as={Link} to="/add-film">
                Add Film
              </Nav.Link>
              <Nav.Link as={Link} to="/edit-film">
                Edit Film
              </Nav.Link>
            </Nav>
          </Navbar.Collapse>
        </Container>
      </Navbar>
      <h1 className="text-center mt-5">Movie List</h1>
      <CardDeck className="mt-4">
        {films.map((film) => (
          <Card key={film.id} className="movie-card">
            <Card.Body>
              <Card.Title>{film.title}</Card.Title>
              <Card.Text>{film.description}</Card.Text>
            </Card.Body>
          </Card>
        ))}
      </CardDeck>
    </div>
  );
};

export default HomePage;
