import React, { useState, useEffect } from "react";
import axios from "axios";
import { Navbar, Nav, Container, Card, Row, Button, Col } from "react-bootstrap";
import { Link } from "react-router-dom";

const deleteMovie = async (movieId) => {
  try {
    await axios.delete(`http://localhost:3000/api/films/${movieId}`);
    fetchMovies();
  } catch (error) {
    console.error(error);
  }
};

const Trending = () => {
  const [movies, setMovies] = useState([]);

  const fetchMovies = async () => {
    try {
      const response = await axios.get("http://localhost:3000/films");
      setMovies(response.data);
    } catch (error) {
      console.log(error);
    }
  };
  useEffect(() => {
    fetchMovies();
  }, []);

  return (
    <div style={{ backgroundColor: "#000", color: "#fff" }}>
      <Navbar variant="dark" bg="dark">
        <Container>
          <Navbar.Brand href="/">UASFILM</Navbar.Brand>
          <Nav>
            <Link to="/add">ADD MOVIE</Link>
            <Nav.Link href="#film">KUMPULAN FILM</Nav.Link>
          </Nav>
        </Container>
      </Navbar>
      <Container id="film">
        <br />
        <h1 className="text-white">FILMS</h1>
        <br />
        <Row>
          {movies.map((movie) => (
            <Col md={4} className="movieWrapper" key={movie.id}>
              <Card className="movieImage">
                <Card.Img variant="top" src={movie.image} />
                <Card.Body>
                  <Card.Title>{movie.title}</Card.Title>
                  <Card.Text>{movie.description}</Card.Text>
                </Card.Body>
                <Card.Footer>
                  <Link to={`edit/${movie.id}`}>Edit</Link>
                  <Button variant="danger" onClick={() => deleteMovie(movie.id)}>
                    Delete
                  </Button>
                </Card.Footer>
              </Card>
            </Col>
          ))}
        </Row>
      </Container>
    </div>
  );
};

export default Trending;
